INSERT INTO `wp_posts` VALUES("901", "1", "2012-09-27 16:08:04", "2012-09-27 16:08:04", "The future of the EN C class glider has been re-written by Niviuk\'s Arktik 3.\r\n\r\nTheir philosophy \"the importance of small details\" is truly expressed through this wing. Placing the pilot at the heart of the design, the Arctik 3 has impeccable handling, bridging the connection, like never before, between wing and man.\r\n\r\nThis confidence thrusting wing flies steady and beautifully in all conditions and makes you feel in control even in turbulent air.  The bench mark has been risen - now go enjoy!\r\n\r\nCheck out the full wing range from <a title=\"Niviuk\" href=\"http://www.niviuk.com/\">Niviuk</a>", "Niviuk Arctik 3", "", "inherit", "closed", "open", "", "897-revision-3", "", "", "2012-09-27 16:08:04", "2012-09-27 16:08:04", "", "897", "http://escpd.com/897-revision-3/", "0", "revision", "", "0");
