INSERT INTO `wp_posts` VALUES("916", "1", "2012-09-26 15:20:42", "2012-09-26 15:20:42", "&lt;DO NOT POST&gt;\n\nThe North Face E-Tip Glove is a form fitting, four-way stretch glove that offers warmth and maximum dexterity for use with touch-type gadgets. For urban wear, you can comfortably work your phone, MP3 player or laptop mouse pad with warm fingers in this glove. The stretch knit shell is ideal for three-season wear. Whatever you need to hold – a rope, ski pole, dog lead, horse reins, ice axe or après ski mulled wine - the silicone grip pattern on the palm of The North Face Etip Glove will help you keep a tight grip on it.\n\n<strong>Feature</strong>\n<ul>\n	<li>Comfortably work your phone, MP3 player or laptop mouse pad with warm hands. 5 Dimensional Fit™</li>\n	<li>Four-way stretch</li>\n	<li>Silicone grip pattern on palm</li>\n</ul>", "Northface Etip", "", "inherit", "closed", "open", "", "864-revision-4", "", "", "2012-09-26 15:20:42", "2012-09-26 15:20:42", "", "864", "http://escpd.com/864-revision-4/", "0", "revision", "", "0");
