INSERT INTO `wp_posts` VALUES("219", "1", "2012-08-28 12:21:00", "2012-08-28 12:21:00", "Zap Skimboards have been building boards since 1983 and have one of the most recognisable names in skimboarding. Crafted by Steve Boomhower, one of Zap\'s team riders and shapers, the Boomhower Pro is Zap\'s most popular Team Series board to date. The Boom Pro is built with 2 layers of carbon fibre for added stength and balance which makes it perform well in all conditions.\r\n\r\nThe Boomhower Pro is available in a number of colours and 5 different sizes.\r\n\r\nFind out more at <a href=\"http://zapskimboards.com/\">www.zapskimboards.com</a>\r\n\r\n", "Zap Boomhower Pro", "", "publish", "closed", "open", "", "zap-boomhower-pro", "", "", "2012-08-29 19:11:39", "2012-08-29 19:11:39", "", "0", "http://escpd.com/?p=219", "0", "post", "", "0");
