INSERT INTO `wp_posts` VALUES("424", "1", "2012-09-06 18:47:57", "2012-09-06 18:47:57", "Sunday Bikes are once again raising the bar and producing some of the best complete bikes on the circuit for 2013. They certainly haven\'t cut any corners with the Soundwave with it\'s cutting edge design - it\'s simply a must have.  This bike is a real team replica and is identical in every way to the bike used by the Sunday team. The technical specification is simply breathtaking and no expense has been spared in the production of this BMX masterpiece.  This makes the Soundwave the top bike on the market and it even comes with a lifetime warranty!\r\n\r\nMoney well spent!\r\n\r\n&nbsp;", "Sunday 2013 Soundwave Special", "", "publish", "closed", "open", "", "sunday-2013-soundwave-special", "", "", "2012-09-08 20:01:05", "2012-09-08 20:01:05", "", "0", "http://escpd.com/?p=424", "0", "post", "", "0");
