INSERT INTO `wp_posts` VALUES("440", "1", "2012-09-03 13:41:26", "2012-09-03 13:41:26", "<h2>Rechargeable headlamp with self-adjusting lighting - REACTIVE LIGHTING technology</h2>\nThe rechargeable NAO headlamp adapts its two high power LEDs instantly and automatically to the lighting needs for greater comfort, fewer manual interventions and longer battery life. NAO is the first Petzl headlamp with REACTIVE LIGHTING technology: a sensor measures and analyzes the ambient light. The OS by Petzl software, free for download at <a title=\"www.petzl.com/OS\" href=\"http://www.petzl.com/OS\">www.petzl.com/OS</a>, allows the NAO’s potential to be maximized with the option to personalize the headlamp and its performance.", "NAO Rechargeable headlamp - Petzl", "", "inherit", "closed", "open", "", "437-revision-2", "", "", "2012-09-03 13:41:26", "2012-09-03 13:41:26", "", "437", "http://escpd.com/437-revision-2/", "0", "revision", "", "0");
