INSERT INTO `wp_posts` VALUES("539", "1", "2012-09-06 18:48:45", "2012-09-06 18:48:45", "Sunday Bikes are once again raising the bar and producing some of the best complete bikes on the circuit for 2013. They certainly haven\'t cut any corners with the Soundwave with it\'s cutting edge design - it\'s simply a must have.  This bike is a real team replica and is identical in every way to the bike used by the Sunday team. The technical specification is simply breathtaking and no expense has been spared in the production of this BMX masterpiece.  This makes the Soundwave the top bike on the market and it even come with a lifetime warranty!\r\n\r\nMoney well spent!\r\n\r\n&nbsp;", "Sunday 2013 Soundwave Special", "", "inherit", "closed", "open", "", "424-revision-13", "", "", "2012-09-06 18:48:45", "2012-09-06 18:48:45", "", "424", "http://escpd.com/424-revision-13/", "0", "revision", "", "0");
