INSERT INTO `wp_posts` VALUES("1654", "1", "2012-12-04 09:20:17", "2012-12-04 09:20:17", "Stay visible and protected this winter with the Hypershield range brought to you by the mighty Nike!\n\nWith reflective fabric you will be able to stay safe during dark winter nights and your friends will be able to see you as you effortlessly run off into the distance, giving them something to aspire to.\n\nThe Nike Hypershield range are engineered with weatherized mesh to give your feet unparalleled protection against extreme elements during the winter months. You can also give your trainer its own personality with NikeID, customising different areas of your trainer to create a truly unique item of sports wear.", "Nike Hypershield Trainers", "", "inherit", "closed", "open", "", "1615-revision-9", "", "", "2012-12-04 09:20:17", "2012-12-04 09:20:17", "", "1615", "http://escpd.com/1615-revision-9/", "0", "revision", "", "0");
