INSERT INTO `wp_posts` VALUES("1741", "1", "2012-12-21 11:32:27", "2012-12-21 11:32:27", "With a name like \'Brute Force\' you expect this machine to be full to the brim with unadulterated power...you will not be disappointed! The Brute Force 750 4x4i EPS is light weight for better performance, handling and fuel efficiency. It\'s immense power, maneuverability, power steering and comfortable riding position makes exploring the back country and trail riding a pleasure not to be missed!\r\n\r\nFinished off with a scratch resistant Realtree® APG™ HD® camouflage paint job, people won\'t even see you coming.\r\n\r\n<a title=\"Kawasaki Brute Force 2013\" href=\"http://www.kawasaki.com/Products/product-specifications.aspx?scid=1&amp;id=684\" target=\"_blank\">Check out the video here</a>", "2013 Brute Force® 750 4x4i EPS Camo", "", "publish", "closed", "open", "", "2013-brute-force-750-4x4i-eps-camo", "", "", "2012-12-21 11:32:27", "2012-12-21 11:32:27", "", "0", "http://escpd.com/?p=1741", "0", "post", "", "0");
