INSERT INTO `wp_posts` VALUES("1853", "1", "2013-01-06 15:37:05", "2013-01-06 15:37:05", "Rule the waves and solo sport riding with the Yamaha FZR® 2013 Wave Runner. This beautiful, rapid cruiser for two combines lightweight NanoXcel® hull and a supercharged 1812cc EFI engine that owns every line, even in the most treacherous waters.\n\nThe FZR® Wave Runner performs outrageously tight turns whilst remaining stable and comfortable. If you are more at home with a stand-up riding style the telescopic steering adjustment allows you to switch easily from a lean-forward style.\n\nThis performance focused machine is accompanied by luxury and girlfriend dumping good looks.", "Yamaha FZR® 2013 Wave Runner", "", "inherit", "closed", "open", "", "1748-revision-7", "", "", "2013-01-06 15:37:05", "2013-01-06 15:37:05", "", "1748", "http://escpd.com/1748-revision-7/", "0", "revision", "", "0");
