INSERT INTO `wp_posts` VALUES("1998", "1", "2013-01-28 10:03:34", "2013-01-28 10:03:34", "Evil is back and better than ever! \r\n\r\nWe are pleased to introduce the Undead, Evil\'s newest downhill frame! The Undead is simply stunning and sets a new level for downhill riding.\r\n\r\nThe Undead is an evolution of it\'s predecessor, the massively successful Revolt but instead of being made from aluminium the Undead is constructed from high pressure moulded carbon fibre making it super light and incredibly strong. \r\n\r\nWe are excited that Evil are back and look forward to seeing more from them in 2013.\r\n\r\n\r\n<img alt=\"Evil_Undead_Frame1\" src=\"http://escpd.com/wp-content/uploads/2013/01/Evil_Undead_Frame1.jpg\" width=\"966\" height=\"594\" />\r\n<br>\r\n<img alt=\"Evil_Undead_Frame1\" src=\"http://escpd.com/wp-content/uploads/2013/01/Evil_Undead_Frame2.jpg\" width=\"966\" height=\"594\" />\r\n<br>\r\n<img alt=\"Evil_Undead_Frame1\" src=\"http://escpd.com/wp-content/uploads/2013/01/Evil_Undead_Frame3.jpg\" width=\"966\" height=\"594\" />", "2013 Evil Undead Frame", "", "publish", "closed", "open", "", "2013-evil-undead-frame", "", "", "2013-01-28 12:01:51", "2013-01-28 12:01:51", "", "0", "http://escpd.com/?p=1998", "0", "post", "", "0");
