INSERT INTO `wp_posts` VALUES("2128", "1", "2013-03-15 08:38:36", "2013-03-15 08:38:36", "There\'s something fundamentally brilliant about Steve Peat.\r\n\r\nHe\'s an amazing rider whose career at the top of the World Downhill Mountain Bike circuit started in the mid-90\'s and, at 38, he\'s still up there and still winning.\r\n\r\nHe\'s also an awesome person who loves a beer, loves his family and loves riding.\r\n\r\nEnthusiasts tend to attract other enthusiasts, so it\'s only fitting that helmet gear supremo designer Troy Lee crafted a carbon and titanium D3 for the former World Champion known as Peaty.\r\n\r\n&nbsp;", "Troy Lee D3 Carbon - Peaty", "", "publish", "closed", "open", "", "troy-lee-d3-carbon-peaty", "", "", "2013-03-15 08:38:36", "2013-03-15 08:38:36", "", "0", "http://escpd.com/?p=2128", "0", "post", "", "0");
